import React from 'react';
import PropTypes from 'prop-types';

export const Timmer = ({startDay, startHour , startMinute, startSecond, fontSyzeNumber, fontSyzetext, backgroudColor, numberColor, textColor}) => {

    var myVar = setInterval(Timer, 1000);
    startDay = 3;
    startHour = 12;
    startMinute = 23;
    startSecond = 24;
    var init = initialisation (startDay,startHour,startMinute,startSecond);

    function initialisation (Day,Hour,Minute,Second){
        document.getElementById("Second").innerHTML = "1";
    };

    function Timer(){
        if (startSecond != 0){
            startSecond--;
            document.getElementById("Second").innerHTML = startSecond;
        }
        else{
            startSecond = 59;
            if (startMinute != 0){
                startMinute--;
                document.getElementById("Minute").innerHTML = startMinute;
            }
            else{
                startMinute = 59;
                if(startHour != 0){
                    startHour--;
                    document.getElementById("Hour").innerHTML = startHour
                }
            }
        }
    }

    return (
        <div className="timmer">
            <div className="line">
                <p>Next game in :</p>
            </div>
            <div className="line">
                <p id="Day"></p>
                <p className="textDay">Days</p>
                <p id="Hour"></p>
                <p className="textHours">Hours</p>
            </div>
            <div className="line">
                <p id="Minute"></p>
                <p className="textMinute">Minutes</p>
                <p id="Second"></p>
                <p className="textSecond">Seconds</p>
            </div>
            
        </div>
    );

};

Timmer.prototype = {

    startDay: PropTypes.string,
};
