                    startHour--;
                    document.getElementById("Hour").innerHTML = startHour
                }
            }
        }*/
    }

    return (
        <div className="timmer">
            <div className="line">
                <p>Next game in :</p>
            </div>
            <div className="line">
                <p id="Day"></p>
                <p className="textDay">Days</p>
                <p id="Hour"></p>
                <p className="textHours">Hours</p>
            </div>
            <div className="line">
                <p id="Minute"></p>
                <p className="textMinute">Minutes</p>
                <p id="Second"></p>
                <p className="textSecond">Seconds</p>
            </div>
            
        </div>
    );

};

Timmer.prototype = {

    startDay: PropTypes.string,
};
