import React from 'react';
import PropTypes from 'prop-types';
import './Timer.css'

class Timer extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            myVar: setInterval(this.Timer, 1000),
            startDay: 0,
            startHour: 0,
            startMinute: 0,
            startSecond: 5,
            tempo: 0,
        }
    };



    Timer() {

        try {
            if (this.state.startDay === 0 && this.state.startHour === 0 && this.state.startMinute === 0 && this.state.startSecond === 0) {
                if (this.state.tempo === 0) {
                    document.getElementById("timer").style.display = "none";
                    document.getElementById("code").style.visibility = "visible";
                    document.getElementById("onclick").style.pointerEvents = "auto";
                    document.getElementById("onclick").style.cursor = "pointer";
                }
                this.codeToornament();
            }
            else {
                document.getElementById("onclick").style.pointerEvents = "none";
                document.getElementById("Second").innerHTML = startSecond;
                document.getElementById("Minute").innerHTML = startMinute;
                document.getElementById("Hour").innerHTML = startHour;
                document.getElementById("Day").innerHTML = startDay;
                if (this.state.startSecond !== 0) {
                    this.state.startSecond--;
                    document.getElementById("Second").innerHTML = startSecond;
                }
                else {
                    this.state.startSecond = 59;
                    document.getElementById("Second").innerHTML = startSecond;
                    if (this.state.startMinute !== 0) {
                        this.state.startMinute--;
                        document.getElementById("Minute").innerHTML = startMinute;
                    }
                    else {
                        this.state.startMinute = 59;
                        document.getElementById("Minute").innerHTML = startMinute;
                        if (this.state.startHour !== 0) {
                            this.state.startHour--;
                            document.getElementById("Hour").innerHTML = startHour;
                        }
                        else {
                            this.state.startHour = 23;
                            document.getElementById("Hour").innerHTML = startHour;
                            this.state.startDay--;
                            document.getElementById("Day").innerHTML = startDay;
                        }
                    }
                }
            }
        } catch (error) {
            clearInterval(this.state.myVar);
        }


    }

    copyLink() {
        var toCopy = document.getElementById("textarea");
        toCopy.querySelector;
        document.execCommand('copy');
        console.log("Copier !");
    }

    codeToornament() {

        if (this.state.tempo !== 5) {
            this.state.tempo++;
        }
        else {
            document.getElementById("timer").style.display = "inline";
            document.getElementById("code").style.visibility = "hidden";
            document.getElementById("linkToornament").disabled = true;
            document.getElementById("onclick").style.cursor = "auto";
            this.state.tempo = 0;
            /* 
            GET TIME UNTIL NEXT GAME
            */
            this.state.startDay = 0;
            this.state.startHour = 0;
            this.state.startMinute = 0;
            this.state.startSecond = 4;
        }
    }

    render() {
        return (
            <div id="linkToornament">
                <div onClick={() => this.copyLink()} id="onclick" className="borderTimer widgetContainer">
                    <div id="timer">
                        <div className="line">
                            <p className="textNextGame">Next game in :</p>
                        </div>
                        <div className="line">
                            <p id="Day" className="number"></p>
                            <p className="texts">Days</p>
                            <p id="Hour" className="number"></p>
                            <p className="texts">Hours</p>
                        </div>
                        <div className="line">
                            <p id="Minute" className="number"></p>
                            <p className="texts">Minutes</p>
                            <p id="Second" className="number"></p>
                            <p className="texts">Seconds</p>
                        </div>
                    </div>
                    <div id="code">
                        Toornament Code
                    </div>
                    <textarea
                        style={{ display: "none" }}
                        id="textarea"
                        value="Example copy for the textarea."
                    />
                </div>
            </div>
        );
    }


};

export default Timer;
