import React, { useState } from 'react';
import ReactMarkdown from 'react-markdown';
import gfm from 'remark-gfm';
import emoji from 'remark-emoji';
import './AnnoncesGL.css';


//https://github.com/remarkjs/react-markdown

function AnnoncesGL() {

    const renderes = {
        code: ({ language, value }) => {
            return <ReactMarkdown language={language} children={value} plugins={[emoji, gfm]} />
        }
    }

    const text = `:GL:LA GROSSE LIGUE J-1 :GL: @everyone @Capitaines

    On y est : dans moins de 24h, vous serez sur la Faille, à disputer vos premiers matchs de la Grosse Ligue :fire: 
    
    Afin de vous permettre de jouer vos matchs sans problèmes, voici quelques rappels : 
    
    - Les games se joueront via des codes tournois présents sur Toornament (voir #faq). Le lobby ne dure qu'une heure, n'y rentrez pas avant 20h :warning: 
    - Les games se lancent à 20h30. 
    - L'équipe adverse sera considérée comme forfaite si elle ne se présente toujours pas à 20h45. 
    
    -> Tous les renames doivent être déclarés à l'un de vos admins avant le début des matchs :come: 
    
    :warning: PENSEZ À FAIRE LA MAJ DE VOTRE LAUNCHER DÈS CE SOIR :warning: `.replace(/\n/g, "\n\n");



    return (
        <div className="textBlock">
            <ReactMarkdown plugins={[emoji, gfm]} renderers={renderes} children={text} className="text scrollbar scrollbar-primary">

            </ReactMarkdown>
        </div>
    );
}

export default AnnoncesGL;
