import React from 'react';
import PropTypes from 'prop-types';
import './AnnoncesGL.css';

export const AnnoncesGL = ({message}) => {

    return (
        <div className="textBlock">
            <div className="borderBlock"> 
                <div id="Text">
                    NOUVEAUTE 2020 @everyone @Capitaines 

                    Grosse nouveauté ce soir : https://gl.vivide.re/

                    C'est un site avec toutes les stats des parties de la GL, joueurs, équipes, etc...
                    Merci à @Vivi pour tout le dev 

                    Nous espérons que l'outil vous plaira (et qu'il vous sera utile), et n'hésitez pas à MP @Vivi en cas de report de bug/autre (et pour le remercier)

                    _________________

                    Pour du plus basique : comme tous les mercredis, petit rappel sur le format du soir !
                    Dragon : 1 seul BO3 
                    Héraut : 1 seul BO3
                    Carapateur : 2 BO1 (round 5 & 6, 21h45 pour le second match au plus tard !)

                    Début des matchs : 20h30
                    Retard maximum autorisé : 15 minutes
                    Déclaration des forfaits : à partir de 20h45 à un ADMIN (en orange) de votre région

                    Les streams sont ON ! Et vous pourrez retrouver tous les streamers Grosse Ligue au même endroit ! Une seule adresse : https://www.twitch.tv/team/lagrosseligue/

                    Bonne soirée à tous, bonne chance et surtout AMUSEZ-VOUS !
                </div>
            </div>
        </div>
    );

};

AnnoncesGL.prototype = {

    message: PropTypes.string,
}
