'use strict';

var homedir = require('os').homedir();

var env = process.env.NODE_ENV || 'prd';
var gitlabUrl = process.env.GITLAB_URL || 'https://git.mdbgo.com';
var projectsDomain = process.env.PROJECTS_DOMAIN || 'mdbgo.io';
var technologies = process.env.BACKEND_TECHNOLOGIES || 'node8,node10,node12,php7.2,php7.3,php7.4';
var databases = process.env.DATABASES || 'mysql8,mongodb';
process.env['NODE_TLS_REJECT_UNAUTHORIZED'] = 0;
process.env['NODE_NO_WARNINGS'] = 1;
module.exports = {
  env: env,
  gitlabUrl: gitlabUrl,
  projectsDomain: projectsDomain,
  databases: databases.split(','),
  backendTechnologies: technologies.split(','),
  tokenFile: '/.auth',
  tokenDir: "".concat(homedir, "/.mdbcli"),
  port: process.env.PORT || 3030,
  host: process.env.HOST || 'localhost'
};